package com.leo.robot;

import android.os.Bundle;
import android.support.v7.app.AppCompatActivity;
import android.view.View;
import android.widget.Button;
import android.widget.TextView;

public class TestActivity extends AppCompatActivity {


    String msg = "";
    String mode = "Marm";
    //String mode = "Farm";

    // String msg="";

    private Button init, move, join, stop;
    private TextView tips;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_test_main);
        init = (Button) findViewById(R.id.btn_init);
        move = (Button) findViewById(R.id.btn_move);
        join = (Button) findViewById(R.id.btn_join);
        stop = (Button) findViewById(R.id.btn_stop);
        tips = (TextView) findViewById(R.id.tips);
        init.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                append(NativeUtils.init(msg, mode));
            }
        });
        move.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                append(NativeUtils.move("ACTION_MOVE_1", mode));
            }
        });
        join.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
               append(NativeUtils.join("ACTION_J0_1", mode));
            }
        });
        stop.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
               append(NativeUtils.stop(mode));
            }
        });

    }

    public void append(String text) {
        tips.append(text + "\n");
    }
}
